#import "Problem08.h"

@implementation Problem08

+ (long)largestProductInASeries {
    NSString *string = @"";
    
    NSInteger largest = 0;
    int lengthOfProduct = 4;
    
    for (int index = 0; index < string.length - lengthOfProduct; index++) {
        NSString *first = [string substringWithRange:NSMakeRange(index, 1)];
        NSInteger result1 = [first integerValue];
        NSString *second = [string substringWithRange:NSMakeRange(index+1, 1)];
        NSInteger result2 = [second integerValue];
        NSString *third = [string substringWithRange:NSMakeRange(index+2, 1)];
        NSInteger result3 = [third integerValue];
        NSString *fourth = [string substringWithRange:NSMakeRange(index+3, 1)];
        NSInteger result4 = [fourth integerValue];
        
        NSInteger product = result1*result2*result3*result4;
        
        if (largest < product) {
            NSLog(@"Sequence is: %@, %@, %@, %@", first, second, third, fourth);
            largest = product;
        }
    }

    return largest;
}

@end
